from code128 import generate_code128_code


def test_code128_letters():
    expected = ""
    assert generate_code128_code("ABCDEFGHIJKLMNOPQRSTUVXYZ") == expected


def test_code128_numbers():
    expected = "1101001000010011101100100111001101100111001011001011100110010011101101110010011001110100111011011101110100110011100101100110000101001100011101011"
    assert generate_code128_code("0123456789") == expected


def test_code128_symbols():
    expected = "110100100001011001110011001000100100110001001100110011010011001110101110011001001000110011011001100100010011001100010010011100100110111011001001111011011010100011110111000110101100100001011100110100110110110001100011011010010011000110011011001101100011010100001100110111011101100011101011"
    assert generate_code128_code(",*'\"./$ %+:;{}[]<>@#!?`") == expected
