from code39 import generate_code39_code


def test_code39_code_for_whole_alphabet():
    expected = ""
    assert generate_code39_code("ABCDEFGHIJKLMNOPQRSTUVXYZ") == expected


def test_code39_numbers():
    expected = "10010110110101010011011010110100101011010110010101101101100101010101001101011011010011010101011001101010101001011011011010010110101011001011010100101101101"
    assert generate_code39_code("0123456789") == expected


def test_code39_symbols():
    expected = "1001011011010110010101101010010010100101001001001010100110101101010100100100101001010010010100101101101"
    assert generate_code39_code("./$ %+") == expected
